CREATE DATABASE /*!32312 IF NOT EXISTS*/`tourism` /*!40100 DEFAULT CHARACTER SET utf8 */;

USE `tourism`;

/*Table structure for table `travelnote` */

DROP TABLE IF EXISTS `travelnote`;

CREATE TABLE `travelnote` (
  `TravelNoteId` int(10) NOT NULL auto_increment,
  `userId` int(10) default NULL,
  `UserName` varchar(20) default '',
  `Tname` varchar(300) NOT NULL default '',
  `TrvaelContnet` text NOT NULL,
  `TravelImg` blob,
  `TravelNoteTime` varchar(50) NOT NULL default '',
  `PraiseNum` int(11) default NULL,
  PRIMARY KEY  (`TravelNoteId`),
  KEY `userId` (`userId`),
  CONSTRAINT `travelnote_ibfk_1` FOREIGN KEY (`userId`) REFERENCES `user` (`UserId`)
) ENGINE=InnoDB AUTO_INCREMENT=62 DEFAULT CHARSET=utf8;

/*Data for the table `travelnote` */

insert  into `travelnote`(`TravelNoteId`,`userId`,`UserName`,`Tname`,`TrvaelContnet`,`TravelImg`,`TravelNoteTime`,`PraiseNum`) values (32,NULL,'12345aa','叫我如何不想她---台湾','\r\n作为南京人\r\n一直有种台湾情结。\r\n刚巧美国打工时候认识不少台湾朋友\r\n于是带着这份情结踏上台湾这块土地。\r\n一来拜访朋友\r\n二来解答心中一直以来的种种疑惑。\r\n由于时间有限\r\n这次只选择了台中，台北，新北\r\n\r\n第一站-台中\r\n在台中待了3天\r\n这三天朋友要么开车要么骑车\r\n载着我到处跑\r\n特别感谢她的接待\r\n让我看到了一个热情热闹美丽的台中\r\n\r\n\r\n\r\n关于【心之芳庭】\r\n知道心之芳庭\r\n是因为蚂蜂窝里面的一个蜂友的帖子\r\n当时看到他的照片\r\n第一印象就是\r\n这个地方我一定要去\r\n因为太美了\r\n事实上也的确没让我们失望\r\n精美的建筑以及成片的绿色\r\n都让我们为之兴奋\r\n于是照片也拍得特别多',NULL,'2016-10-24 20:01:55',NULL),(34,NULL,'lichen',' 最后悔的就是来了——《帕劳》','帕劳共和国，台湾人称她为帛琉。位于菲律宾以东太平洋上，距离菲律宾约800公里，距离台湾约1600公里，香港约2700公里，是个群岛，包含200多个岛屿，其中9个岛屿是经常有人居住的，帕劳在历史上曾先后被西班牙、德国、日本所统治，二战后托管给美国，1994年独立。帕劳居民约20000人，百分之七十是本地土著，百分之三十是菲律宾人，语言上通行英语，在货币上通行美元。\r\n\r\n 在帕劳你会发现日本人居多，还有韩国人和台湾人。因为台湾与帕劳距离很近，交往也密切，帕劳几乎所有的物质都是靠进口，其中有一部分就是来自台湾。我们大陆人来帕劳旅行，找个好的导游是个关键的问题，要找成立久的和多年服务台湾客人的地接社，毕竟我们大陆今年赴帕劳的游客才增多，所以找这样的地接社，导游服务质量很好，我们玩的也开心。我们选择的是帕劳当地很有名气的一家KBT地接社，之前就是一直服务台湾客人了，导游的中文和英文都不错，就是国语说的带有台湾的腔调，这也许就是服务了太多的台湾游客造成的。我是来帕劳之前就在国内提前半个月就定好了，给我们定出海行程的是小颖，来之前许多不太了解的也是问她，人还蛮实在的东北女孩。之前要是早点找到小颖机票和酒店也就一起在她那定了，我俩还能省1000多。',NULL,'2016-10-23 20:39:55',NULL),(36,NULL,'zhaoliyin','彩云之南——昆明、大理、丽江5日游走','台历交替之际\r\n回忆着刚过去的一年走过的路\r\n7段旅途，4个国家，13个城市\r\n意外的发现似乎是中了海岛的魔咒吧\r\n一月三亚，六月济州岛，八月嵊泗，十月涠洲岛，以及上个月的巴厘岛\r\n一半的时间都在过夏天\r\n\r\n一年来和许小佳同学的恋爱算是走过磨合期尘埃落定\r\n我并不认为那些爱情里陪你到最后的必须是你最爱亦或称之为对的人\r\n世界那么大，人来人往皆是缘\r\n缘分来了，他走近你\r\n缘分尽了，他离开你\r\n能伴你同行的，是在你最需要他时恰巧出现在你身边\r\n能给你拥抱和温暖的人\r\n这些年，兜兜转转\r\n有辜负、有迷惘、有罢了\r\n有暗恋、有表白、有得不到、有道不明\r\n也许一个人要走很多的路\r\n经历过生命中无数突如其来的繁华与苍凉才会变得成熟\r\n\r\n我会艳羡青梅竹马的爱情\r\n也会感激参与我的人生向我走来的每一个身影\r\n那些教会我付出，包容，改变，收敛光芒，磨平棱角\r\n让我体会了怦然心动，心如死灰后仍相信真爱不灭\r\n人的一生\r\n都有一些说不出的秘密，挽不回的遗憾\r\n触不到的梦想，忘不了的爱\r\n这些都是时光给予的馈赠\r\nI want to be your favoriet hello and your hardest goodbye',NULL,'2016-10-21 20:49:55',NULL),(37,NULL,'zhaoliyin','触不到的梦想，忘不了的爱','因为台湾与帕劳距离很近，交往也密切，帕劳几乎所有的物质都是靠进口，其中有一部分就是来自台湾。我们大陆人来帕劳旅行，找个好的导游是个关键的问题，要找成立久的和多年服务台湾客人的地接社，毕竟我们大陆今年赴帕劳的游客才增多，所以找这样的地接社，导游服务质量很好，我们玩的也开心。我们选择的是帕劳当地很有名气的一家KBT地接社，之前就是一直服务台湾客人了，导游的中文和英文都不错，就是国语说的带有台湾的腔调，这也许就是服务了太多的台湾游客造成的。我是来帕劳之前就在国内提前半个月就定好了，给我们定出海行程的是小颖，来之前许多不太了解的也是问她，人还蛮实在的东北女孩。之前要是早点找到小颖机票和酒店也就一起在她那定了，',NULL,'2016-10-25 20:00:51',NULL),(50,NULL,'2_差有有有','勇闯摩洛哥（十一天自由行之六城纪事+冗长tips 全篇完）','<p>开头的话</p><p><br/></p><p><br/></p><p>摩洛哥归来已经整整半个月，路上就策划的游记却迟迟没有动笔，几乎每天呆坐窗前冥思苦想。之前在穷游写过九篇了还从来没有这么</p><p><br/></p><p>纠结过呢！不知怎地今天没来由哼起了那句老歌“想说爱你并不是很容易的事， 那需要太多的勇气， 想说忘记你也不是很容易的事，&nbsp;</p><p><br/></p><p>我只有伫立在风中，想你……”</p><p><br/></p><p>……真是好契合好契合现在的心情啊，标题顺势就落在鲜亮亮的“勇”字上，OK啦，那就开篇吧！</p><p><br/></p><p>是什么吸引我们去摩洛哥？小时候读的《一千零一夜》？早年看过的《北非谍影》？三毛的西撒哈拉？还是Bertie Higgins那略带忧郁的《卡萨布兰卡》……唔，都是，也都不是，说来也简单得不要不要的，居然只是一张照片里的色彩，没错，就是舍夫沙万那一往情深的蓝、蓝、蓝……</p><p><br/></p><p>天（摄于沃卢比利斯罗马古城遗迹）</p><p><img src=\"/images/ueditor/792904702986686464.jpg\" alt=\"MN2.jpg\"/></p><p><br/></p><p>地（摄于舍夫沙万城外）</p><p><img src=\"/images/ueditor//792896392841531392.jpg\" alt=\"792896392841531392.jpg\"/></p><p><br/></p><p>墙之梦（摄于马拉喀什的里亚德）<img src=\"/images/ueditor//792896433748578304.jpg\" alt=\"792896433748578304.jpg\"/></p><p><br/></p><p>花之梦（摄于马拉喀什马约尔花园）</p><p><img src=\"/images/ueditor//792897468915388416.jpg\" alt=\"792897468915388416.jpg\"/></p><p><br/></p><p>交通：</p><p>&lt;城际交通&gt;</p><p>首选火车，虽稍贵于长途大巴，但有空调比较舒适，即便有晚点现象但时间上大体可控。一般选择二等车厢即可。</p><p><br/></p><p>摩洛哥铁路“ONCF”网站地址：http://www.oncf.ma/Pages/Accueil.aspx</p><p><br/></p><p>次选长途大巴，最好选择CTM公司，车况好运行和服务比较正规，我们因车次的原因坐过的Nejme Chamal公司稍差。</p><p><br/></p><p>末选出租车，司机素质无法预测，经历过的高下落差很大，且普遍缺乏契约精神，到达时多要钱或不想找零的现象几乎全覆盖。</p><p><br/></p>',NULL,'2016-10-31 09:44:25',NULL),(51,NULL,'zhaoliyin','印度最西北，是梦中的克什米尔【从克什米尔南下喜马偕尔，瑞诗凯诗的30天印度之旅】','<p>克什米尔，是个小梦</p><p><br/></p><p>2011年底，我第一次到印度。那是我间隔年的第4个月，曼谷的飞机到加尔各答。从出机场开始，之前旅行的倦怠好似突然就一扫而空。到达背包客聚集的旅馆，开始一个月的义工生活，每天感受着穿越市集的混乱嘈杂但是生机勃勃。在满街仅占一席之地却足以谋生的小摊，和印度人来来往往的穿梭姿态中，脸上一直挂着笑容，再不经意地得到笑容的回应。那也是我第一次开始了解印度。</p><p><br/></p><p>随后的旅行，走走停停，住了各种沙发，感受了各种温情。那时年轻，没有成见，只把所见，当作当然。在大街上走着走着，突然停下来拿一把干草喂一匹正在休息的拉车的马；或者走路时要绕过发呆的牛，小心避让满地的牛粪，也是只觉兴奋快乐，未觉是天下大奇。</p><p><br/></p><p>三个月就这么过得飞快。大家都说，对于印度，要么爱，要么恨。遇到太多来了无数次，或者待了很久很久的旅行者，他们的爱恨显而易见。而我，也许注定是要用把签证用到最后一天，来表达这个国家所带来的复杂感情。一定会再回来的，那时心里，早已如此认定。</p><p><br/></p><p>在加尔各答旅馆的多人间看了第二遍《Three Idiots》（在后来又看了好多次），开头配着音乐的山路风景，和结尾高原上的学校，恍如仙境的雪山蓝湖，在那时就已把拉达克这个名字，种在了心底。无奈正当冬季，大雪封山，只有盼着下次，等待它开山的短暂的夏季。只是并不知下次，到底是何时。</p><p><br/></p><p>14年房车旅行，在新藏线途径班公错，后来从新疆出境巴基斯坦，又经过风之谷和吉尔吉特。当时只觉遗憾，没有去到K2的大本营斯卡杜。后来才知道，这些地方竟都和克什米尔有关。虽然知道有过战争，现在依旧是争议边界，但那时的“克什米尔”这个词，对于我来说就是雪山峡谷和充满野性的美景，就像它美丽的名字一样。所以还曾发下宏愿说想要绕着新疆塔什库尔干，巴基斯坦的克什米尔，印度的克什米尔，再到西藏走个大环线。被人戏谑说还没等你走到边境线，早就被乱枪打死。于是，印度，拉达克，克什米尔，就像在我心里，编起了一首小合唱。直到今年夏天，朋友说计划了印度旅行。“印度”这个名字，让这首小合唱，又阻挡不住地登上舞台。</p><p><br/></p><p>“那今年的假期，就去克什米尔吧！”</p><p>结果，朋友的印度之旅不小心烂尾，倒是圆了我的克什米尔小梦。</p><p><br/></p><p>画笔下的克什米尔之旅</p><p><br/></p><p>因为又开始学画画，所以这次旅行带上了画纸和水彩。这样子，夹子能用他的相机记录，而我也能用我的画笔记录我们的旅行。所以，这次就以画的形式先睹为快好了。</p><p><img src=\"/images/ueditor//792901899409035264.jpg\" alt=\"792901899409035264.jpg\"/></p><p><br/></p><p>在斯利那加，达尔湖上划着小船游荡。湖中一片片飘满的睡莲，被当地人称作“漂浮的花园”。总觉得莲叶漂浮在水面上，像一块块小盘子，也许装些水珠，也许只装了达尔胡的清透空气。</p><p><img src=\"/images/ueditor/792906042337005568.jpg\" alt=\"L2.jpg\"/></p><p><br/></p><p><br/></p><p>旅程和花销</p><p><img src=\"/images/ueditor/792906214680956928.jpg\" alt=\"L3.jpg\"/></p><p><br/></p><p>其实整段旅程，只有11天是真正在克什米尔地区，后来就继续南下了。但是因为它是旅行的缘起，也算是圆了一个小梦，所以还是叫它</p><p><br/></p><p>作克什米尔之旅吧！</p><p><br/></p><p>这次旅行，我们从在到达新德里机场的两小时后，就直接飞往了斯利那加。斯利那加5天，遇到大罢工。</p><p><br/></p><p>接着16小时的车程去到列城。列城6天，又是18小时的车程到达喜马偕尔邦的蒙拉里（蒙拉里就是地图里翻译的马纳利也是穷游翻译的默纳利，至于为什么是蒙拉里，就是个后面会讲的小故事了）。</p><p>蒙拉里4天，接着一整天的大巴到达西姆拉，喜马偕尔邦首府，一日游之后，转了小火车大火车，到达了恒河边的圣城瑞诗凯诗。为了学瑜伽和冥想，我临时改变计划，在瑞诗凯诗待了10天。夹子则提前回国。</p><p><br/></p>',NULL,'2016-10-31 09:49:46',NULL),(52,NULL,'lichen','寻找麦哲伦｜菲律宾（宿务＋薄荷岛＋马尼拉）完','<p>曾经有一个葡萄牙人，他坚信大地是圆形的，盼望做一件人类历史上破天荒的壮举——环绕地球航行。他向皇室提出了这个请求，结果葡萄牙皇室不鸟他，觉得没这必要。于是，他投奔西班牙。</p><p><br/></p><p>西班牙人乐了，又可以开辟东方新航线，又可以寻找香料，有何不可？塞维利亚的要塞司令非常欣赏他，答应了他的请求，并把女儿也嫁给了他。次年，这个年轻人的到了国王的接见，当然他的情商也确实不错——没有空手去——他给国王送上了一只精致的地球仪。国王心花怒放，马上替他组建船队。</p><p><br/></p><p>这个年轻人就是麦哲伦。</p><p><br/></p><p>当人们谈起人类第一次环球航行的时候，总会提及这个名字。而事实上，麦哲伦并没有完成全球航行，而是在他到达宿务的时候，插手小岛首领之间的内讧，被土著打死了。</p><p><br/></p><p>作为个一心想要环游世界的人，因为麦哲伦的故事，我在印象中第一次记住了宿务这个名字，并且列在了菲律宾的must see list里。</p><p><br/></p><p>4月去了菲律宾，先上一点图：</p><p><img src=\"/images/ueditor/792907942138613760.jpg\" style=\"\"/></p><p><img src=\"/images/ueditor/792907942243471360.jpg\" style=\"\"/></p><p><img src=\"/images/ueditor/792907942176362496.jpg\" style=\"\"/></p><p><br/></p><p>圣婴教堂 Basilica Minore del Santo Niño</p><p><br/></p><p>圣婴教堂，顾名思义，重点就是“圣婴”，也就是小耶稣。据说当时麦哲伦登陆宿雾，第一批土著接受洗礼的时候，麦哲伦送给首领的老婆这个小耶稣像。后来这里发生过一场大火，几乎所有的东西都付之一炬，只有这座圣婴像还完好无损。被士兵发现后，当地人便把它供奉在教堂里，视为他们的保护神。</p><p><br/></p><p>圣婴教堂，建于1565年，这是菲律宾历史最悠久的教堂。但是之后它被大火烧了3次，现在看到的是18世纪重建的。后来啊，在2013年宿雾经历了一场地震，教堂上的bell又掉下来了，又重建……</p><p><br/></p><p>我到达的星期五，正巧碰到的弥撒，人多的简直让人震惊。人生第一次看到弥撒啊，当时在胡志明的教堂等半天，都没等到。这次真是无心插柳柳成荫。</p><p><img src=\"/images/ueditor/792908389041704960.jpg\" style=\"\"/></p><p><img src=\"/images/ueditor/792908389138173952.jpg\" style=\"\"/></p><p><br/></p><p><br/></p>',NULL,'2016-10-31 09:58:42',NULL),(53,NULL,'12345aa','【对世界上瘾】这可能是一篇美国东海岸很小小小小小众的游记（马里兰、弗吉尼亚、华盛顿七天六晚）','<table width=\"100%\" style=\"border-collapse: collapse; border-spacing: 0px; table-layout: fixed;\"><tbody><tr><td class=\"editor bbsDetailContainer\" style=\"margin: 0px; padding: 0px; outline: none; line-height: 26px; color: rgb(50, 50, 50); word-wrap: break-word; word-break: break-all;\"><h1 id=\"post-15939952-0\" style=\"margin: 22px 0px; padding: 0px; outline: none; font-size: 24px; line-height: 30px; font-family: &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, 宋体, sans-serif; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">写在前面的话</h1><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; outline: none; line-height: 26px; word-wrap: break-word; word-break: break-all; font-family: &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, 宋体, sans-serif; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">之前去过不少次<a href=\"http://place.qyer.com/usa/\" class=\"bbsPlaceLabel\" target=\"_blank\" data-bn-ipg=\"bbs-thread-placeLink-236\" style=\"text-decoration: none; color: rgb(50, 50, 50); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(50, 50, 50);\">美国</a>了，这次是第四次来到美国的土地上，借着去<a href=\"http://place.qyer.com/washingtondc/\" class=\"bbsPlaceLabel\" target=\"_blank\" data-bn-ipg=\"bbs-thread-placeLink-82\" style=\"text-decoration: none; color: rgb(50, 50, 50); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(50, 50, 50);\">华盛顿</a>公差的机会，想在美国首都地区去走一些这个即小众又被世人熟知的地方。首都地区是有华盛顿<a href=\"http://place.qyer.com/colombia/\" class=\"bbsPlaceLabel\" target=\"_blank\" data-bn-ipg=\"bbs-thread-placeLink-453\" style=\"text-decoration: none; color: rgb(50, 50, 50); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(50, 50, 50);\">哥伦比亚</a>特区、弗吉尼亚州，<a href=\"http://place.qyer.com/mali/\" class=\"bbsPlaceLabel\" target=\"_blank\" data-bn-ipg=\"bbs-thread-placeLink-500\" style=\"text-decoration: none; color: rgb(50, 50, 50); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(50, 50, 50);\">马里</a>兰州这三地方组成，这里一度为国际中心地和美国人民的家园，华盛顿特区虽然不大，但是这里有世界一流的博物馆和<a href=\"http://z.qyer.com/deal/77304/\" class=\"bbsLmLabel\" data-info=\"[{&quot;product_type&quot;:2410,&quot;id&quot;:77304,&quot;weight&quot;:1,&quot;url&quot;:&quot;http:\\/\\/z.qyer.com\\/deal\\/77304\\/&quot;,&quot;title&quot;:&quot;\\u7f8e\\u56fd\\u534e\\u76db\\u987f\\u7eaa\\u5ff5\\u7891\\u9884\\u7559\\u5165\\u573a\\u95e8\\u7968+\\u7eaa\\u5ff5\\u9986\\u534a\\u65e5\\u6e38&quot;,&quot;price&quot;:&quot;399&quot;,&quot;picture&quot;:&quot;http:\\/\\/pic.qyer.com\\/public\\/supplier\\/jd\\/2016\\/06\\/01\\/14647666907087&quot;,&quot;highlight&quot;:&quot;1\\u30014\\u5c0f\\u65f6\\u534e\\u76db\\u987f\\u7eaa\\u5ff5\\u7891\\u4e4b\\u65c5&lt;br \\/&gt;&lt;br /&gt;2\\u3001\\u7ad9\\u5728150\\u591a\\u7c73\\u9ad8\\u7684\\u89c2\\u666f\\u53f0\\u4e0a\\u4eab\\u53d7\\u90fd\\u5e02\\u5168\\u8c8c\\u98ce\\u5149&lt;br \\/&gt;&lt;br /&gt;3\\u3001\\u4eb2\\u773c\\u770b\\u5230\\u660e\\u4fe1\\u7247\\u4e0a\\u7684\\u57ce\\u5e02\\u6807\\u793a\\u6027\\u5efa\\u7b51&quot;}]\" target=\"_blank\" data-bn-ipg=\"bbs-thread-lmLink-纪念馆\" style=\"text-decoration: none; color: rgb(50, 50, 50); position: relative; margin-right: 2px; border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(50, 50, 50);\">纪念馆</a>、独特的社区文化。在<a href=\"http://place.qyer.com/poi/V2wJYVFuBzRTbQ/\" class=\"bbsPlaceLabel\" target=\"_blank\" data-bn-ipg=\"bbs-thread-placeLink-82829\" style=\"text-decoration: none; color: rgb(50, 50, 50); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(50, 50, 50);\">独立纪念碑</a>东西两头从<a href=\"http://place.qyer.com/poi/V2EJalFlBzdTYA/\" class=\"bbsPlaceLabel\" target=\"_blank\" data-bn-ipg=\"bbs-thread-placeLink-59314\" style=\"text-decoration: none; color: rgb(50, 50, 50); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(50, 50, 50);\"></a><a href=\"http://z.qyer.com/deal/57509/\" class=\"bbsLmLabel\" data-info=\"[{&quot;product_type&quot;:2410,&quot;id&quot;:57509,&quot;weight&quot;:1,&quot;url&quot;:&quot;http:\\/\\/z.qyer.com\\/deal\\/57509\\/&quot;,&quot;title&quot;:&quot;\\u3010\\u7ecf\\u5178\\u65e5\\u6e38\\u3011\\u7f8e\\u56fd\\u534e\\u76db\\u987f\\u5e02\\u533a\\u7ecf\\u5178\\u4e00\\u65e5\\u6e38&quot;,&quot;price&quot;:&quot;160&quot;,&quot;picture&quot;:&quot;http:\\/\\/pic.qyer.com\\/public\\/supplier\\/lastminute\\/2015\\/10\\/26\\/14458292665166&quot;,&quot;highlight&quot;:&quot;&lt;p&gt;&lt;strong&gt;&lt;span style=\\&quot;color: red;\\&quot;&gt;&lt;\\/span&gt;&lt;\\/strong&gt;&lt;\\/p&gt;&lt;p&gt;\\u5168\\u7a0b\\u4ea4\\u901a\\u63a5\\u9001\\u670d\\u52a1&lt;\\/p&gt;&lt;p&gt;&lt;span style=\\&quot;color: red;\\&quot;&gt;\\uff083\\u5468\\u5c81\\u4ee5\\u4e0b\\u513f\\u7ae5\\u514d\\u8d39\\uff0c3-11\\u5468\\u5c81\\u53ef\\u8d2d\\u4e70\\u513f\\u7ae5\\u4ef7\\uff0c12\\u5468\\u5c81\\u53ca\\u4ee5\\u4e0a\\u8bf7\\u8d2d\\u4e70\\u6210\\u4eba\\u4ef7\\uff09&lt;\\/span&gt;&lt;br\\/&gt;&lt;\\/p&gt;&quot;}]\" target=\"_blank\" data-bn-ipg=\"bbs-thread-lmLink-国会山\" style=\"text-decoration: none; color: rgb(50, 50, 50); position: relative; margin-right: 2px; border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(50, 50, 50);\">国会山</a>走到<a href=\"http://place.qyer.com/poi/V2EJalFlBzNTZA/\" class=\"bbsPlaceLabel\" target=\"_blank\" data-bn-ipg=\"bbs-thread-placeLink-59350\" style=\"text-decoration: none; color: rgb(50, 50, 50); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(50, 50, 50);\"></a><a href=\"http://z.qyer.com/deal/57509/\" class=\"bbsLmLabel\" data-info=\"[{&quot;product_type&quot;:2410,&quot;id&quot;:57509,&quot;weight&quot;:1,&quot;url&quot;:&quot;http:\\/\\/z.qyer.com\\/deal\\/57509\\/&quot;,&quot;title&quot;:&quot;\\u3010\\u7ecf\\u5178\\u65e5\\u6e38\\u3011\\u7f8e\\u56fd\\u534e\\u76db\\u987f\\u5e02\\u533a\\u7ecf\\u5178\\u4e00\\u65e5\\u6e38&quot;,&quot;price&quot;:&quot;160&quot;,&quot;picture&quot;:&quot;http:\\/\\/pic.qyer.com\\/public\\/supplier\\/lastminute\\/2015\\/10\\/26\\/14458292665166&quot;,&quot;highlight&quot;:&quot;&lt;p&gt;&lt;strong&gt;&lt;span style=\\&quot;color: red;\\&quot;&gt;&lt;\\/span&gt;&lt;\\/strong&gt;&lt;\\/p&gt;&lt;p&gt;\\u5168\\u7a0b\\u4ea4\\u901a\\u63a5\\u9001\\u670d\\u52a1&lt;\\/p&gt;&lt;p&gt;&lt;span style=\\&quot;color: red;\\&quot;&gt;\\uff083\\u5468\\u5c81\\u4ee5\\u4e0b\\u513f\\u7ae5\\u514d\\u8d39\\uff0c3-11\\u5468\\u5c81\\u53ef\\u8d2d\\u4e70\\u513f\\u7ae5\\u4ef7\\uff0c12\\u5468\\u5c81\\u53ca\\u4ee5\\u4e0a\\u8bf7\\u8d2d\\u4e70\\u6210\\u4eba\\u4ef7\\uff09&lt;\\/span&gt;&lt;br\\/&gt;&lt;\\/p&gt;&quot;},{&quot;product_type&quot;:2410,&quot;id&quot;:77304,&quot;weight&quot;:1,&quot;url&quot;:&quot;http:\\/\\/z.qyer.com\\/deal\\/77304\\/&quot;,&quot;title&quot;:&quot;\\u7f8e\\u56fd\\u534e\\u76db\\u987f\\u7eaa\\u5ff5\\u7891\\u9884\\u7559\\u5165\\u573a\\u95e8\\u7968+\\u7eaa\\u5ff5\\u9986\\u534a\\u65e5\\u6e38&quot;,&quot;price&quot;:&quot;399&quot;,&quot;picture&quot;:&quot;http:\\/\\/pic.qyer.com\\/public\\/supplier\\/jd\\/2016\\/06\\/01\\/14647666907087&quot;,&quot;highlight&quot;:&quot;1\\u30014\\u5c0f\\u65f6\\u534e\\u76db\\u987f\\u7eaa\\u5ff5\\u7891\\u4e4b\\u65c5&lt;br \\/&gt;&lt;br /&gt;2\\u3001\\u7ad9\\u5728150\\u591a\\u7c73\\u9ad8\\u7684\\u89c2\\u666f\\u53f0\\u4e0a\\u4eab\\u53d7\\u90fd\\u5e02\\u5168\\u8c8c\\u98ce\\u5149&lt;br \\/&gt;&lt;br /&gt;3\\u3001\\u4eb2\\u773c\\u770b\\u5230\\u660e\\u4fe1\\u7247\\u4e0a\\u7684\\u57ce\\u5e02\\u6807\\u793a\\u6027\\u5efa\\u7b51&quot;}]\" target=\"_blank\" data-bn-ipg=\"bbs-thread-lmLink-林肯纪念堂\" style=\"text-decoration: none; color: rgb(50, 50, 50); position: relative; margin-right: 2px; border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(50, 50, 50);\">林肯纪念堂</a>这段路可以了解整个美国。弗吉尼亚州既有仙那度大烟山这两大公园给了弗吉尼亚带来了更多的绿色，弗吉尼亚特有的度假风情使其独具精彩魅力。在宽广的大海踏浪逐沙，或是悠然自得的在海边晒太阳，绵延14英里的海岸线满足所有对度假的所有期待与幻想。行驶在弗吉尼亚的丛林中时候，丛林的山道格外美丽。马里兰州除了海洋城的海滩外，还有<a href=\"http://place.qyer.com/baltimore-usa/\" class=\"bbsPlaceLabel\" target=\"_blank\" data-bn-ipg=\"bbs-thread-placeLink-7887\" style=\"text-decoration: none; color: rgb(50, 50, 50); border-bottom-width: 1px; border-bottom-style: dotted; border-bottom-color: rgb(50, 50, 50);\">巴尔的摩</a>这个美东文化名城，这里有美丽的海港和不少反映美国早期历史的文化珍品和纪念遗址。这条环跨三州，有人文，有历史，有海滩，有风光的<a href=\"http://z.qyer.com/car/\" class=\"bbsBizLabel\" target=\"_blank\" data-bn-ipg=\"bbs-thread-businessLink-38\" style=\"color: rgb(0, 115, 182);\">自驾</a>之旅就这么开始了，并不是一条常规的线路，如果你是大众景点爱好者，可能这个线路不大适合你，但是如果你是一个<a href=\"http://z.qyer.com/car/\" class=\"bbsBizLabel\" target=\"_blank\" data-bn-ipg=\"bbs-thread-businessLink-38\" style=\"color: rgb(0, 115, 182);\">自驾</a>爱好者而且更喜欢一些小众的项目，这条线适合你，这里可以带你走过在美国历史上最重要的几个州，体验大西洋的海水和美式度假沙滩的感觉，这次很遗憾没有赶上在大烟山的萤火虫之旅，就差了那么几天的时间差，如果有下一次我一定多两天时间去看一趟大烟山的萤火虫们（听说今年都开始摇号了）。一次非主流还算不错的<a href=\"http://z.qyer.com/car/\" class=\"bbsBizLabel\" target=\"_blank\" data-bn-ipg=\"bbs-thread-businessLink-38\" style=\"color: rgb(0, 115, 182);\">自驾</a>就这么开始了。</p></td></tr></tbody></table><p><br/></p><p>华盛顿纪念碑</p><p><br/></p><p><img src=\"/images/ueditor//792910102838513664.jpg\" alt=\"792910102838513664.jpg\"/></p><p><br/></p><p>弗吉尼亚海滩水族馆</p><p><br/></p><p><img src=\"/images/ueditor//792910224536244224.jpg\" alt=\"792910224536244224.jpg\"/></p><p><br/></p><p>阿萨提格国家海岸公园</p><p><br/></p><p><img src=\"/images/ueditor//792910457559191552.jpg\" alt=\"792910457559191552.jpg\"/></p><p><br/></p><p>关于签证和美国入关</p><p><br/></p><p>美签申请方式论坛里有很多详细介绍了，我之前已经有过美国签证了，所以这次就不详细介绍美签的申请，唯一需要提及的是我这次由于中间去了趟社会主义好兄弟古巴，所以这次出现了两次入关的情况，两次入关给我的待遇完全不一样，一次白人老头基本上一句话都没问我就让我过去了，一次是黑人大哥，问了我将近10分钟，最后都跟我聊上天了。总结一句话就是白人还是蛮懒的，入关都懒得搭理你。现在美国入关已经不需要填写入境单了，只需填写一张海关申报单，去程航班会根据出发地提供相应地区语种和英语两种，我在北京飞DC就拿到的是和中文和英语，在巴拿马飞回来就是西班牙语和英语，Anyway就算没有拿到你适合的语言，海关入境大厅也会有多语种让你去选择。</p><p><br/></p>',NULL,'2016-10-31 10:13:09',NULL),(54,NULL,'2_差有有有','额济纳胡杨林，那一抹亮眼的金黄色','<p>既然在年初感受了春天的美妙，那么到了秋天自然要去採下秋色，这样才完美。除了银杏树、白桦林，更能使人赞叹的就是胡杨林了。</p><p>胡杨林被称为“沙漠英雄树”，生而不死一千年，死而不倒一千年，倒而不朽一千年。比较热门看胡杨林的地方一是新疆塔里木胡杨林，另一个就是内蒙的额济纳胡杨林了，考虑到新疆路途较远和安全问题，我们选择了后者。</p><p>行程：兰州——张掖丹霞国家地质公园——马蹄寺——嘉峪关——金塔胡杨林——額济纳胡杨林——居延海——祁连山——西宁</p><p><br/></p><p><br/></p><p><img src=\"/images/ueditor/792916714965831680.jpeg\" style=\"\"/></p><p><img src=\"/images/ueditor/792916714886139904.jpeg\" style=\"\"/></p><p><br/></p><p>到达兰州后，去到马子禄牛肉面吃下第一碗兰州牛肉面，“一清二白三红四绿五黄”是牛肉面的特色：一清（汤清）、二白（萝卜白）、三红（辣椒油红）、四绿（香菜、蒜苗绿）、五黄（面条黄亮）。但是我和先生一直认为后面吃到的舌尖尖牛肉面更好吃。</p><p><br/></p><p><img src=\"/images/ueditor/792917187160576000.jpeg\" alt=\"j3.jpeg\"/></p><p><br/></p><p><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; line-height: 25px; widows: 1;\">马蹄寺地处</span><a href=\"http://www.mafengwo.cn/travel-scenic-spot/mafengwo/11635.html\" class=\"link _j_keyword_mdd\" data-kw=\"祁连山\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration: none; cursor: pointer; outline: 0px; font-size: 14px; border-bottom: 1px solid rgb(148, 142, 142); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; line-height: 25px; white-space: normal; widows: 1;\">祁连山</a><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; line-height: 25px; widows: 1;\">脚下，距今已逾1600年。传说天马下凡时踩在了这里的一块岩石上，蹄印留下，因而得名。而传说中的马蹄印迹现存于普光寺马蹄殿内，成为镇寺之宝。</span><br style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\"/><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; line-height: 25px; widows: 1;\">三十三天佛洞是马蹄寺最主要的建筑，沿山开凿了七层洞窟，洞窟层层叠叠，每层之间都有隧道相通。走进佛洞，沿着内部昏暗的阶梯一层层向上，可以看到众多的古老佛像。不过在“文化大革命”时期寺院损失惨重，石窟佛像、壁画破坏殆尽，现在看到的多为改革开放之后重新修复，万幸的是大雄宝殿、站佛殿、观音殿、药师殿等多为明万历年间旧迹。</span><br style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\"/><br style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\"/><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; line-height: 25px; widows: 1;\">山中的颜色已经丰富起来了。</span></p><p><img src=\"/images/ueditor/792917509757079552.jpeg\" style=\"\"/></p><p><img src=\"/images/ueditor/792917509845159936.jpeg\" style=\"\"/></p><p><br/></p><p><br/></p>',NULL,'2016-10-31 10:35:04',NULL),(55,NULL,'lichen','【带上孩子世界环游】之诺亚方舟停靠的地方，那是亚美尼亚失去的远方','<p>亚美尼亚是这次带着问问四国行的第二站，也是我此行在到达之前最期待的一个国家，那个在圣经中记载下的诺亚方舟的美丽传说，那个仿佛海市蜃楼一般与荒野之中的修道院融为一体的亚拉圣山，无不让人魂牵梦绕想要置身于这般幻境之中。<br/></p><p><br/></p><p>我的朋友常说回来听你讲讲在路上的故事呗，可我每次听到这句话时，总觉得一定是三五人围成一桌，好酒满上，牛逼吹起来，搞笑的故事讲起来，惊险的历程描述的绘声绘色外加表情肢体相配合，仿佛让听的人也进到故事里一般的精彩。可是……可是我真的不是个口述故事的好手，估摸着再美好的故事都能被我讲的平淡无趣吧。</p><p><img src=\"/images/ueditor/792977391076118528.jpg\" alt=\"d1.jpg\"/></p><p><br/></p><p>好多人问你为什么这么多年一直在写写写，记录着自己的小情怀和小生活。那是因为和喜欢独处，喜欢一个人旅行一样，书写也是一种与内心对话最好的方式之一。</p><p><br/></p><p>尽管升为人母之后的独处时光越来越少，但我仍常常在深夜的一盏灯下，喝一杯自己的酒，写一段只给自己看的话。那感觉真好，不留恋过去，做着当下世俗标准之外却是内心想要做着的事，以及一切随缘的未来。</p><p><br/></p><p><img src=\"/images/ueditor/792977572794339328.jpg\" alt=\"d2.jpg\"/></p><p><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; outline: none; color: rgb(50, 50, 50); line-height: 26px; word-wrap: break-word; word-break: break-all; font-family: &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, 宋体, sans-serif; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">就好像带孩子旅行，好坏的声音都有，有人说好厉害，太有勇气了，有人说不过是自己想玩又没人带孩子，这么小的孩子没记忆的，折腾孩子等等等等。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; outline: none; color: rgb(50, 50, 50); line-height: 26px; word-wrap: break-word; word-break: break-all; font-family: &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, 宋体, sans-serif; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">听到赞美的话时其实我的内心是觉得言过其实有点羞愧的。因为总觉得即便只是言语上的夸奖，那也一定是自己做了能让别人受益的付出才受得起这样的赞美。可是……可是不管是旅行还是带着孩子，那不过是千百种生活方式中微小的一种，只是一件很私人和小到不值得去惊扰别人的事情。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; outline: none; color: rgb(50, 50, 50); line-height: 26px; word-wrap: break-word; word-break: break-all; font-family: &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, 宋体, sans-serif; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">而对于后一种声音，同样要感恩能有这般的机会看到这个社会存在的各种不一样的声音，世界那么多条路，不管是崎岖还是艰难最后总会通到终点。珍惜那些路上遇见的，彼此欣赏或可以同行的人，和那些志趣不相投的人微笑着挥手。这便是我处事待人的坚持。</p><p><br/></p><p><img src=\"/images/ueditor/792977935928791040.jpg\" alt=\"d3.jpg\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; outline: none; color: rgb(50, 50, 50); line-height: 26px; word-wrap: break-word; word-break: break-all; font-family: &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, 宋体, sans-serif; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">这个小生命的降临，让我越来越多的看到这个世界的单纯美好，让我知道幸福不单是自己的诗和远方，更是护送我的他去往他的远方。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; outline: none; color: rgb(50, 50, 50); line-height: 26px; word-wrap: break-word; word-break: break-all; font-family: &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, 宋体, sans-serif; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; outline: none; color: rgb(50, 50, 50); line-height: 26px; word-wrap: break-word; word-break: break-all; font-family: &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, 宋体, sans-serif; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">问问一直很皮实，别人都说我在散养孩子所以他的适应能力比较强，但是无论是生活还是旅行总不会永远随着人意，亚美尼亚的那段时间，问问第一次在旅途中生病，至今回想起那个他细弱的声音喊着妈妈的场景，那个我把他拥在怀里渴望将他抱得更紧一点，却始终觉得抱得还不够的心情，想到这里眼泪便又开始湿润眼眶，但是那一刻包括现在也还是并无后悔与退缩，反倒更确定了会带着他一直走下去。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; outline: none; color: rgb(50, 50, 50); line-height: 26px; word-wrap: break-word; word-break: break-all; font-family: &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, 宋体, sans-serif; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; outline: none; color: rgb(50, 50, 50); line-height: 26px; word-wrap: break-word; word-break: break-all; font-family: &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, 宋体, sans-serif; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">一个人的一生那么长，没有人会料到有多少风浪，也没有人知道躲避的另一端是否又是一个新的灾难。相比于给予他多少财富与所谓安定的生活环境，我更想……在还能陪伴于他左右的日子里，让他慢慢的、一点一点体会什么是承担与勇敢。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; outline: none; color: rgb(50, 50, 50); line-height: 26px; word-wrap: break-word; word-break: break-all; font-family: &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, 宋体, sans-serif; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; outline: none; color: rgb(50, 50, 50); line-height: 26px; word-wrap: break-word; word-break: break-all; font-family: &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, 宋体, sans-serif; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">孩子，不急的，你可以慢慢长大。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; outline: none; color: rgb(50, 50, 50); line-height: 26px; word-wrap: break-word; word-break: break-all; font-family: &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, 宋体, sans-serif; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">妈妈的肩膀虽然狭小，可能会承不起你越来越重的分量，但是妈妈的内心足够宽广，可以成为你随时想要内心依靠和歇息的港湾。</p><p><br/></p>',NULL,'2016-10-31 14:35:02',NULL),(57,NULL,'2_差有有有','遥远的秘境——2014年12月三江并流之旅','<p><br/></p><p>时间为12月10日-18日，选择冬季去，主要原因是每年10到3月是三江并流的旱季，晴天多，看到梅里等雪山的机会也多，另外江水比较清澈，不像夏天雨季的时候是黄色的，其实三江并流的江水四季有四个颜色，我们来的时候才刚刚变清澈，呈现出蓝绿色，但还没有变成最美的宝蓝色。这里最好的季节是2、3月份，那时江水完全是冰川融水，颜色是宝石一样的蓝色，而各种花儿盛开，庄稼和树长出新绿，将是一片更缤纷绚烂的色彩。</p><p>&nbsp;</p><p>此行前六天几乎都是蓝天白云，空气清澈，阳光耀眼，如同在青藏高原。云南的几大雪山全部都看到了。上一回夏天经过此地，一座雪山也没看到，一直在雨区之中，季节选择真的非常重要。不过，虽然晴朗的天气有利于全面再现远处的风景，但这里的云雾也很美，尤其是怒江这样的地方，《德拉姆》基本上就都是在夏季云雾缭绕的天气中拍摄的，云雾营造出了更加神秘、遥远的气氛。所以到怒江的话，不必为天气发愁，阴晴雨雪都有不同的味道。只要雨不太大，影响能见度即可。另外在高山峡谷区，下雨会增加出行难度和地质灾害风险，所以雨量最大的时候还是要谨慎。</p><p><br/></p><p>路上的风景</p><p><br/></p><p>玉龙雪山：</p><p><img src=\"/images/ueditor/795252883808456704.jpeg\" title=\"\" alt=\"you2.jpeg\"/></p><p><br/></p><p>梅里雪山：</p><p><img src=\"/images/ueditor/795253144098574336.jpeg\" alt=\"you3.jpeg\"/></p><p><br/></p><p>丽江西面上高速去石鼓，其中有一段向北，沿线风景特别美，远处有一座雪山，可能是另一角度的玉龙，也可能是哈巴。天很蓝（对于东部人来说），阳光下山腰和河谷上的村庄，错落有致的房屋在树木的掩映之中，房子都是传统的建筑，可惜在高速上没法停下拍照。真心觉得，到丽江玩的同志们还不如多跑跑这些附近的村庄，丽江周围，美景还是有不少。</p><p>&nbsp;</p><p>大约三十公里后下高速，沿着弯曲的山路下坡，就看见金沙江了。上一次看到金沙江是在夏季，那是汹涌的黄色，这一次，江水宁静得多，像镜子般倒映着蓝天。去石鼓镇的一路都在江边，沿着山崖和江水蜿蜒前行，峰回路转，不断有让人惊奇的景色。对于习惯了东部地区丘陵和平原风貌的人来说，横断山和三江并流区的高山深峡，每一次遇到都会感到震撼。</p><p><br/></p>',NULL,'2016-11-06 21:15:51',NULL),(58,NULL,'zhaoliyin','青岛亲子游','<p>孩子暑假开始，为了不影响我九月进藏的假期。就近选择了青岛，全家自驾游。</p><p>第一天上午九点从连云港出发，十二点多到的宾馆。在艺龙定的青岛润航宾馆，（岳阳路10号）非常适合带车的游客，价位合适，酒店周边环境也不错。周边好多德式建筑，绿化更不用说了</p><p><br/></p><p><img src=\"/images/ueditor/795254866527260672.jpeg\" alt=\"y0.jpeg\"/><br/></p><p>路过的胶州湾大桥，大桥起自青岛主城区海尔路，经红岛到黄岛，大桥全长36.48公里，投资额近100亿，历时4年完工。全长超过我国杭州湾跨海大桥 &nbsp;和美国切萨皮克跨海大桥，是当今世界上最长的跨海大桥，也是世界第二长桥。大桥于2011年6月30日全线通车。2011年上榜吉尼斯世界纪录 &nbsp;和美国“福布斯 &nbsp;”杂志，荣膺“全球最棒桥梁”荣誉称号。（图片来源于网络）<br/></p><p>&nbsp; 刚开始我们并不知道这个大桥有多长，只是觉得总是开不到头。百度了一下，原来如此。</p><p><br/></p><p><br/></p><p><img src=\"/images/ueditor/795255340970151936.jpeg\" alt=\"y2.jpeg\"/></p><p>公主楼，坐落于居庸关 &nbsp;路16号的公主楼，精巧可爱，就像是童话 &nbsp;故事中的常有的模样，也许正是丹麦建筑风格才造就了著名童话作家安徒生 &nbsp;。公主楼建造于本世纪三十年代中期，占地近千平方米，建筑面积721.98平方米。 本世纪20年代，丹麦王国 &nbsp;曾在青岛设立领事馆。1929年，丹麦王子乘豪华客轮“飞欧尼亚”号到青岛，遍游青岛市区。他经过八大关一带时，见到此处风景宜人，濒临大海，宜于度假休养，遂令凡麦驻青领事赵亨 &nbsp;先生在此购地建造了这座丹麦风格的建筑。原准备请丹麦公主来青岛避暑消夏，但事实上，丹麦公主并未来过，然而&quot;公主楼&quot;的名字却从此流传出去。该建筑为北欧 &nbsp;滨海风格庭院建筑。主楼为砖木结构，地下一层地上三层，花岗岩砌基，屋脊双面陡坡呈尖耸状，开有可远眺海滨的气窗，墨绿色粉刷墙面上的楼层窗外，由白绿色马赛克 &nbsp;嵌饰框边，使建筑造型简捷、流畅、精巧、活泼。室内有壁橱、质地考究的木扶梯、地板及墙裙，房间小巧明亮，起居设施完备。室外西南有宽阔的草坪，衬托主建筑高雅、醒目、别具一格。<br/></p><p><br/></p><p><img src=\"/images/ueditor/795255546495242240.jpeg\" alt=\"y3.jpeg\"/></p><p><br/></p><p>儿子对教堂什么的一直很感兴趣，第二天又去了天主教堂，仿佛来到了童话世界。</p><p>&nbsp; &nbsp;青岛基督教堂，它是一座典型的德国古堡式建筑，由钟楼和礼堂两部分组成，主要材料是花岗岩。钟楼高39·1米，在上面可以观看远处大海的景色，里面的礼堂宽敞明亮，能容纳1000多人，大厅高18米，两侧分为楼上楼下两层。 该基督教堂原来是为德国人聚会礼拜的场所。1897年，德国占领胶澳地区后，德国“路德会”的柏林教会派了一位传教士来青岛传教。这位传教士在青岛的“总督府”与“总督楼”之间选了一块地方，开始建造基督教堂。最终于1908年4月19日动工，1910年10月23日竣工，历时近两年半。由于该教堂是专供德国信徒聚会礼拜的，所以人们把它称为“德国礼拜堂”。1925年它的全部教产被转让给美国基督教信义会协会，于是该基督教堂就成为这一地区外国人进行宗教活动的场所，因此后来又被称为“国际礼拜堂”。 1949年以后，它成为中国基督教徒的主要活动场所。近一些年来，随着东西方文化的不断融合，基督教在青岛已成为颇有影响的宗教，所以这里的宗教活动非常活跃。</p><p><br/></p><p><br/></p>',NULL,'2016-11-06 21:25:48',NULL),(59,NULL,'2_差有有有','新德里·斋普尔·阿格拉·穿越时空遇见你','<p>&nbsp;在印度这个特别的国度，尤其是德里，人身安全尤其需要注意（印度有合法卖枪支的商店），毕竟连约翰内斯堡的酒店都不需要安检，而在德里，进入酒店或者商场都是要经过安检的。但总的说来，印度人民给人喜爱拍照以及友善的印象。</p><p>&nbsp; &nbsp; 印度的天气比较燥热，比如我们去的这段时间，早晚比较凉，尤其是德里，下午比较热（长袖长裤比较合适），斋普尔和阿格拉也是早晚较凉，但气温整体比德里高，尤其下午，非常晒也很热。</p><p>&nbsp; &nbsp; 在德里和阿格拉，不知道是因为水没净化干净还是其他什么原因，自来水是咸的。我因为之前看到网上的信息有些害怕，所以自己从国内托运了好多矿泉水囧。但我们团有人就是当地自来水烧开了喝，没有出现拉肚子和任何不适的情况。</p><p>&nbsp; &nbsp; 如今印度的金三角这三个城市，公共厕所也是有的，而且还蛮干净，只是需要收取大约10卢比（1RMB)的费用。</p><p>&nbsp; &nbsp; 至于路边摊什么的，我觉得只要你去那个地方见了那个环境，自己就不会再对路边摊有任何兴趣噜。</p><p>&nbsp; &nbsp; 印度有百货商场，但是据说没有超市。导游的解释是，印度有很多开小商店的老板，他们不允许印度出现超市，这样会抢了他们的生意，新德里曾经有一个富二代开了一家超市，结果没几天就发生了爆炸。</p><p><br/></p><p><br/></p><p>第一日 初到德里</p><p><br/></p><p>&nbsp;不知道在每个人心里，印度印象是个什么样子？我去之前，身边任何一个朋友、亲戚，甚至范围扩大到认识的人，他们没有一个人去过这个地方。我唯一的认知全都来源于网络。我以为印度极度脏乱差，臭气熏天，毕竟之前小伙伴的小伙伴说一定要带香水！没有厕所，交通极其混乱。我带着各种忐忑踏上了前往印度的飞机。</p><p>&nbsp; &nbsp; 但至少，印度给人的第一印象几乎是颠覆的，浅色的地毯铺在地上，宽敞的候机大厅。以及，最特别的入境处，——</p><p>&nbsp; &nbsp; 那一整排神的手指。</p><p>&nbsp; &nbsp; 它们真真正正的告诉你，欢迎你来到印度。这个神无所不在的国度。</p><p>&nbsp; &nbsp; 有小伙伴记得阿米尔汗演的那部印度电影吗，《我的个神啊》。这次印度之行让我深深意识到，这部电影对于印度来讲，就是天大的大不敬！！</p><p><br/></p><p><img src=\"/images/ueditor/795257313920421888.jpeg\" alt=\"y1.jpeg\"/></p><p><br/></p><p></p><p>我们到达的第一个景点——贾玛清真寺，位于脏乱破旧的旧德里地区，穿过狭窄脏乱的小巷，它就安静的像在历史中一般。是的，它就像历史，甚至它周围的一切都像历史，那种陈旧褪色、探险寻宝的历史。</p><p>&nbsp; &nbsp; 据说这座清真寺能容纳一万多人同时坐在里面，也是与沙特阿拉伯的麦加大清真寺、埃及开罗的爱资哈尔大清真寺齐名的世界三大清真寺之一。由莫卧儿王朝的第五位皇帝沙贾汗下令于1650年开始建造，历时6年时间建成，整个建筑由赤砂岩和白色大理石组合而成。</p><p>&nbsp; &nbsp; 要进清真寺需要注意穿着，上衣要有衣袖，下装要过膝盖。当然那里也有租衣服的地方，租赁费大概在50卢比，并非一次性那种衣服，同时需要脱鞋进入。这个清真寺最诡异的地方就是拍照，如果你要带拍照设备进入，每个设备300卢比。</p><p><img src=\"/images/ueditor/795257852368392192.jpeg\" alt=\"y2.jpeg\"/><br/></p><p><br/></p><p>&nbsp;清真寺外各种脏乱差，挑战你的忍耐极限！但好在，虽然脏乱差，可至少没有臭味</p><p><img src=\"/images/ueditor/795257989157228544.jpeg\" alt=\"y3.jpeg\"/></p><p><br/></p><p>从贾玛清真寺出来，我们坐上人力车，前往月光集市。据说这里曾经是莫卧儿王朝沙贾汗时期最繁华的集市。因为过去这里做金银生意的人很多，又叫月光大道（月光族大道吧2333）。我们去的时间很早，基本上店铺都没有开门（他们的营业时间大概在10:00am—8:00pm），大多都是在狭窄巷道边摆摊的小贩，有卖水果蔬菜生活用具的，还有卖袋装土司的。</p><p>&nbsp; &nbsp;天呀，当你坐在人力车上，穿行在狭窄到只能容一辆人力车通过的月光集市之间，偶尔与一头全白高大的神牛对撞而过，真有种穿梭时光的真切感觉，那一刻彷如回到了岁月之前，你不曾生活在的那个历史。</p><p>&nbsp; &nbsp;我不敢多拍照啊，怕吓跑你们</p><p>&nbsp; &nbsp;用别人的话说就是，当她看见一位美女从月光集市的小巷走出来，她一整天的心情都没有了，总觉得那样的美女不该生活在这样恶心到令人作呕的环境中。</p><p>&nbsp; &nbsp;这就是月光集市，多么美丽的名字，多么脏乱差的小巷。我在印度的六天时光，这里是最最最让我无法忍受的，印度的其他地方和这里一比，什么都好说了。</p><p>&nbsp; &nbsp;当时我坐在人力车上，看着前面印度小哥努力踩踏着脚踏板，想起许多似曾相识的镜头。人潮喧动的月光集市，在历史上会是个什么样子呢？或者说，历史上人类聚居的那些地方又是什么样子呢，会像如今旧德里这般吗？</p><p><br/></p>',NULL,'2016-11-06 21:34:49',NULL),(60,NULL,'My_Name_丽','凝结的时光 错落的景致 优雅的气韵：一机一镜捷奥十日 电车 歌剧 博物馆 米其林 维也纳机场退税指南','<p>CK，Hallstatt，Salzburg：错落的景致</p><p><br/></p><p>起伏的错落赋予这里极致的景致。</p><p>在城市，登高为你奉上开阔的视野</p><p>而在这里，自然铺展的山地缓坡，绵延而丰厚，绿树红瓦别致的村落</p><p>于险峻山地之上的古老攻防，纵然今日平静，难掩曾经的风云际会</p><p>起伏的山地配上红瓦和河流，就是CK，小城最拥挤的几平米地方，傍晚7点，静得可以凝望</p><p><br/></p><p><img src=\"/images/ueditor/795261481997438976.jpg\" style=\"\"/></p><p><img src=\"/images/ueditor/795261481951301632.jpg\" style=\"\"/></p><p><br/></p><p>起伏的山地配上皮划艇、帆船、快艇、水上悠闲的或飞驰掠过的人们，这里就是圣沃夫冈湖，明媚的湖水不能少了葱郁的岸坡和欢笑声</p><p><img src=\"/images/ueditor/795261707911041024.jpg\" alt=\"y3.jpg\"/></p><p><br/></p><p>捷奥行，选择了办奥地利申根签证。虽然奥地利签证并不简单，但是捷克听闻需要的材料更多。。。这次还真是第一次为了办签证花钱去做公证。</p><p><br/></p><p>做公证是个很麻烦的事情，看到奥地利签证中心挂的申请材料check list里，明明白白写着结婚证需要做公证。于是带着结婚证去公证处，做一本的公证还是两本结婚证都做公证呢？纠结了一下，就做了一本。</p><p><br/></p><p>两周后公证书取回来，还要去外交部再做认证。我后来才知道这叫做双认证。问问外交部的认证办理代理吧，“我只做了一本结婚证的公证，可以吗？”，人家回答的倒是干脆，说你问签证中心啊，问我干嘛呢？于是电话问奥地利签证中心相同问题，人家却回答说，夫妻两人如果都能提供各自的银行流水的话，只需要结婚证的复印件就可以了。。。</p><p><br/></p><p>这么搞笑呢？那check list上为什么就写了要做公证呢，囧。但是为了保险起见，既然公证的钱都花了，就把外交部认证做了呗。递交认证的时候才发现搞笑了，原来的结婚证上不知道为什么没有政府公职人员签字，汗，公证需要重新做。</p><p><br/></p>',NULL,'2016-11-06 21:49:33',NULL),(61,NULL,'12345aa','【走遍世界的尽头】-最全冰岛圣经－极光、温泉、电影、环岛野游','<p>这个可以体会雷克雅未克首都，一个充满艺术气息的北欧城市，看看建筑，比如有名的正态分布大教堂，著名的雕塑，黄金圈可以去Geysir间歇泉、Gullfoss黄金瀑布，欧美大陆大裂缝Þingvellir National Park （美剧《冰与火》长城的原型），还有一些典型的冰岛瀑布Seljalandsfoss塞纳加蓝瀑布，Skógafoss瀑布，Blue Lagoon泻湖温泉。</p><p><br/></p><p><img src=\"/images/ueditor/795264839332270080.jpg\" alt=\"y1.jpg\"/></p><p><br/></p><p>Golden Circle是下图冰岛地图阴影的西南角</p><p><img src=\"/images/ueditor/795265039274741760.jpg\" alt=\"y2.jpg\"/></p><p>10天（在冰岛的时间，不算飞机），是一个环岛绕一圈的基础时间，沿途筛选一些自己喜欢的，最牛逼不能错过的基本也都在下图上了，包含了首都雷克雅未克，首都黄金圈，西部地区，还加了Vík的玄武岩黑沙滩，Vatnajökull欧洲最大冰盖，可以去Skaftafell冰川徒步（星际穿越拍摄地），Jökulsárlón冰湖（007《Die Another Day》拍摄地）， Höfn吃龙虾，Seydisfjordur最美峡湾小镇（《百日梦想家》拍摄地，这个镇子+93号公路，超牛滑板山路，电影里的景都是镇上实景，不是假的搭出来的，人家就住在那样的环境里，让楼主看到了什么是高度发达的社会主义），Dettifoss普罗米修斯的拍摄地，Myvatn米湖躺在温泉里看极光，顺便去《冰与火》囧Snow和女野人来一发的地下温泉洞去约个会，冰岛第二大城市Akureyri物价真是便宜，BenJerry和哈根达斯冰激凌，3-50块巨大一桶。</p><p><br/></p><p>这个楼主最推荐的玩法，之前的两个错过了好多牛逼的东西，可能是因为楼主对城市游无感。所以是在这个线路基础上，玩的加强版，之后有线路图。</p><p><br/></p>',NULL,'2016-11-06 22:03:17',NULL);

